mapper.select:
SELECT id, ware_name, committime, paymenttime, order_numb, order_status, totalmoney, user_id, paymethod, consignee, phone, province, city,
address, remark, backgroundremark, aduit_status, check_man, check_time, service_status, service_manager
FROM `order`

    AND account = #{condition.account}
    AND password = #{condition.password}

SELECT id, ware_name, committime, paymenttime, order_numb, order_status, totalmoney, user_id, paymethod, consignee, phone, province, city, address, remark, backgroundremark, aduit_status, check_man, check_time, service_status, service_manager FROM `order`
ware_name LIKE CONCAT(CONCAT('%', #{keyword}), '%') OR user_id LIKE CONCAT(CONCAT('%', #{keyword}), '%')
ORDER BY #{sort} DESC LIMIT ${start},${limit}

mapper.update:
UPDATE `order`
ware_name=#{ware_name}, committime=#{committime}, paymenttime=#{paymenttime}, order_numb=#{order_numb}, order_status=#{order_status}, totalmoney=#{totalmoney}, user_id=#{user_id}, paymethod=#{paymethod}, consignee=#{consignee}, phone=#{phone}, province=#{province}, city=#{city}, address=#{address}, remark=#{remark}, backgroundremark=#{backgroundremark}, aduit_status=#{aduit_status}, check_man=#{check_man}, check_time=#{check_time}, service_status=#{service_status}, service_manager=#{service_manager},
WHERE id = #{id}

mapper.insert:
INSERT INTO `order` (
ware_name, committime, totalmoney, user_id, consignee, phone, province, city, address
,paymenttime
,order_numb
,order_status
,paymethod
,remark
,backgroundremark
)VALUES(
#{ware_name},#{committime},#{totalmoney},#{user_id},#{consignee},#{phone},#{province},#{city},#{address}
,#{paymenttime}
,#{order_numb}
,#{order_status}
,#{paymethod}
,#{remark}
,#{backgroundremark}
)

    SELECT LAST_INSERT_ID() AS ID
